    my $a = CreateArea;
    my $t = $a->CreateTree;
    my $b = $t->allocBlock(31, 30, 29);
    K(data => 0x33)->dIntoZ(31, 4);
    $t->lengthIntoKeys(31, K length =>0x9);
    $t->putBlock($b, 31, 30, 29);
    $t->getBlock($b, 25, 24, 23);
    PrintOutRegisterInHex 25;
